hooks/use-toast.ts.content:
'use client'

// Inspired by react-hot-toast library
import * as React from 'react'

import type { ToastActionElement, ToastProps } from '@/components/ui/toast'

const TOAST_LIMIT = 1
const TOAST_REMOVE_DELAY = 1000000

type ToasterToast = ToastProps & {
  id: string
  title?: React.ReactNode
  description?: React.ReactNode
  action?: ToastActionElement
}

const actionTypes = {
  ADD_TOAST: 'ADD_TOAST',
  UPDATE_TOAST: 'UPDATE_TOAST',
  DISMISS_TOAST: 'DISMISS_TOAST',
  REMOVE_TOAST: 'REMOVE_TOAST',
} as const

let count = 0

function genId() {
  count = (count + 1) % Number.MAX_SAFE_INTEGER
  return count.toString()
}

type ActionType = typeof actionTypes

type Action =
  | {
      type: ActionType['ADD_TOAST']
      toast: ToasterToast
    }
  | {
      type: ActionType['UPDATE_TOAST']
      toast: Partial<ToasterToast>
    }
  | {
      type: ActionType['DISMISS_TOAST']
      toastId?: ToasterToast['id']
    }
  | {
      type: ActionType['REMOVE_TOAST']
      toastId?: ToasterToast['id']
    }

interface State {
  toasts: ToasterToast[]
}

const toastTimeouts = new Map<string, ReturnType<typeof setTimeout>>()

const addToRemoveQueue = (toastId: string) => {
  if (toastTimeouts.has(toastId)) {
    return
  }

  const timeout = setTimeout(() => {
    toastTimeouts.delete(toastId)
    dispatch({
      type: 'REMOVE_TOAST',
      toastId: toastId,
    })
  }, TOAST_REMOVE_DELAY)

  toastTimeouts.set(toastId, timeout)
}

export const reducer = (state: State, action: Action): State => {
  switch (action.type) {
    case 'ADD_TOAST':
      return {
        ...state,
        toasts: [action.toast, ...state.toasts].slice(0, TOAST_LIMIT),
      }

    case 'UPDATE_TOAST':
      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === action.toast.id ? { ...t, ...action.toast } : t,
        ),
      }

    case 'DISMISS_TOAST': {
      const { toastId } = action

      // ! Side effects ! - This could be extracted into a dismissToast() action,
      // but I'll keep it here for simplicity
      if (toastId) {
        addToRemoveQueue(toastId)
      } else {
        state.toasts.forEach((toast) => {
          addToRemoveQueue(toast.id)
        })
      }

      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === toastId || toastId === undefined
            ? {
                ...t,
                open: false,
              }
            : t,
        ),
      }
    }
    case 'REMOVE_TOAST':
      if (action.toastId === undefined) {
        return {
          ...state,
          toasts: [],
        }
      }
      return {
        ...state,
        toasts: state.toasts.filter((t) => t.id !== action.toastId),
      }
  }
}

const listeners: Array<(state: State) => void> = []

let memoryState: State = { toasts: [] }

function dispatch(action: Action) {
  memoryState = reducer(memoryState, action)
  listeners.forEach((listener) => {
    listener(memoryState)
  })
}

type Toast = Omit<ToasterToast, 'id'>

function toast({ ...props }: Toast) {
  const id = genId()

  const update = (props: ToasterToast) =>
    dispatch({
      type: 'UPDATE_TOAST',
      toast: { ...props, id },
    })
  const dismiss = () => dispatch({ type: 'DISMISS_TOAST', toastId: id })

  dispatch({
    type: 'ADD_TOAST',
    toast: {
      ...props,
      id,
      open: true,
      onOpenChange: (open) => {
        if (!open) dismiss()
      },
    },
  })

  return {
    id: id,
    dismiss,
    update,
  }
}

function useToast() {
  const [state, setState] = React.useState<State>(memoryState)

  React.useEffect(() => {
    listeners.push(setState)
    return () => {
      const index = listeners.indexOf(setState)
      if (index > -1) {
        listeners.splice(index, 1)
      }
    }
  }, [state])

  return {
    ...state,
    toast,
    dismiss: (toastId?: string) => dispatch({ type: 'DISMISS_TOAST', toastId }),
  }
}

export { useToast, toast }

app/api/auth/login/route.ts.content:
import { type NextRequest, NextResponse } from "next/server"
import { getSupabaseClient } from "@/lib/db"

export async function POST(request: NextRequest) {
  const supabase = getSupabaseClient()
  try {
    const { email, password } = await request.json()

    if (!email || !password) {
      return NextResponse.json({ message: "Email and password required" }, { status: 400 })
    }

    const { data: user, error } = await supabase
      .from("users")
      .select("id, name, email, role, barcode")
      .eq("email", email)
      .eq("password", password)
      .single()

    if (error || !user) {
      return NextResponse.json({ message: "Invalid email or password" }, { status: 401 })
    }

    const response = NextResponse.json(
      {
        id: user.id,
        name: user.name,
        email: user.email,
        role: user.role,
      },
      { status: 200 },
    )

    response.cookies.set("auth_user", JSON.stringify({ id: user.id, name: user.name, role: user.role }), {
      httpOnly: true,
      secure: process.env.NODE_ENV === "production",
      sameSite: "lax",
      maxAge: 60 * 60 * 24 * 7,
    })

    return response
  } catch (error) {
    console.error("[v0] Login error:", error)
    return NextResponse.json({ message: "Login failed" }, { status: 500 })
  }
}

app/api/assistants/pay-rewards/route.ts.content:
import { type NextRequest, NextResponse } from "next/server"
import { getSupabaseClient } from "@/lib/db"

export async function POST(request: NextRequest) {
  try {
    const { userId } = await request.json()
    const supabase = getSupabaseClient()

    if (!userId) {
      return NextResponse.json({ message: "Missing userId" }, { status: 400 })
    }

    // Verify user is admin
    const { data: user } = await supabase.from("users").select("role").eq("id", userId).single()

    if (!user || user.role !== "admin") {
      return NextResponse.json({ message: "Unauthorized" }, { status: 403 })
    }

    // Reset all assistant scores to zero
    const { error } = await supabase.from("users").update({ apples: 0 }).eq("role", "assistant")

    if (error) {
      return NextResponse.json({ message: "Failed to reset scores" }, { status: 500 })
    }

    return NextResponse.json({
      success: true,
      message: "All assistant scores have been reset to zero",
    })
  } catch (error) {
    console.error("[v0] Pay rewards error:", error)
    return NextResponse.json({ message: "Internal server error" }, { status: 500 })
  }
}

app/scanner/page.tsx.content:
"use client"

import type React from "react"

import { useEffect, useState, useRef } from "react"
import { useRouter } from "next/navigation"
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card"
import { Input } from "@/components/ui/input"
import { Button } from "@/components/ui/button"
import { useToast } from "@/hooks/use-toast"
import Link from "next/link"
import { Html5QrcodeScanner, Html5QrcodeScanType, Html5QrcodeSupportedFormats } from "html5-qrcode"

interface ScanResult {
  success: boolean
  message: string
  type?: string
  name?: string
  apples?: number
  studentId?: string
  assistantId?: string
}

export default function ScannerPage() {
  const [barcode, setBarcode] = useState("")
  const [loading, setLoading] = useState(false)
  const [result, setResult] = useState<ScanResult | null>(null)
  const [userName, setUserName] = useState("")
  const [userRole, setUserRole] = useState("")
  const [cameraActive, setCameraActive] = useState(false)
  const [applesLoading, setApplesLoading] = useState(false)
  const [isScanning, setIsScanning] = useState(false)
  const [cameraLoading, setCameraLoading] = useState(false)
  const [videoReady, setVideoReady] = useState(false)
  const [autoStarted, setAutoStarted] = useState(false)
  const scannerRef = useRef<Html5QrcodeScanner | null>(null)
  const router = useRouter()
  const { toast } = useToast()

  useEffect(() => {
    // Get user info from session storage
    const userId = sessionStorage.getItem("userId")
    const storedName = sessionStorage.getItem("userName")
    const storedRole = sessionStorage.getItem("userRole")

    if (!userId || !storedName || !storedRole) {
      router.push("/login")
      return
    }

    setUserName(storedName)
    setUserRole(storedRole)
  }, [router])

  // Auto-start camera and scanning best-effort on first load
  useEffect(() => {
    if (autoStarted) return
    ;(async () => {
      try {
        await startCamera()
        setAutoStarted(true)
        await startBarcodeScanning()
      } catch {
        // Ignore failures; user can manually start
        setAutoStarted(true)
      }
    })()
  }, [autoStarted])

  // Cleanup scanner on unmount
  useEffect(() => {
    return () => {
      if (scannerRef.current) {
        try { scannerRef.current.clear() } catch {}
      }
    }
  }, [])


  const startCamera = async () => {
    setCameraLoading(true)
    try {
      // Check if getUserMedia is supported
      if (!navigator.mediaDevices || !navigator.mediaDevices.getUserMedia) {
        throw new Error("Camera not supported on this device")
      }

      // Test camera access
      const stream = await navigator.mediaDevices.getUserMedia({
        video: { 
          facingMode: "environment",
          width: { ideal: 640, min: 320 },
          height: { ideal: 480, min: 240 }
        },
        audio: false
      })

      // Stop the test stream
      stream.getTracks().forEach(track => track.stop())

      setCameraActive(true)
      setIsScanning(false)
      setCameraLoading(false)

      toast({
        title: "Camera Ready",
        description: "Camera is ready. Click 'Start Scanning' to scan barcodes automatically.",
      })
    } catch (error) {
      console.error("Camera error:", error)
      setCameraLoading(false)

      let errorMessage = "Unable to access camera. Please check permissions."
      if (error instanceof Error) {
        if (error.name === 'NotAllowedError') {
          errorMessage = "Camera permission denied. Please allow camera access and try again."
        } else if (error.name === 'NotFoundError') {
          errorMessage = "No camera found on this device."
        } else if (error.name === 'NotSupportedError') {
          errorMessage = "Camera not supported on this device or browser."
        }
      }

      toast({
        title: "Camera Error",
        description: errorMessage,
        variant: "destructive",
      })
    }
  }

  const stopCamera = () => {
    if (scannerRef.current) {
      try { scannerRef.current.clear() } catch {}
      scannerRef.current = null
    }
    setCameraActive(false)
    setIsScanning(false)
    setVideoReady(false)
  }

  const startBarcodeScanning = async () => {
    setIsScanning(true)
    toast({
      title: "Scanning Started",
      description: "Point the camera at a barcode to scan automatically.",
    })

    try {
      // Ensure container exists (in case state hasn't rendered yet)
      if (!document.getElementById("scanner-container")) {
        await new Promise((resolve) => setTimeout(resolve, 100))
      }

      scannerRef.current = new Html5QrcodeScanner(
        "scanner-container",
        {
          fps: 12,
          qrbox: { width: 250, height: 250 },
          aspectRatio: 1.0,
          // Prefer environment camera and support 1D barcodes
          supportedScanTypes: [Html5QrcodeScanType.SCAN_TYPE_CAMERA],
          formatsToSupport: [
            Html5QrcodeSupportedFormats.QR_CODE,
            Html5QrcodeSupportedFormats.EAN_13,
            Html5QrcodeSupportedFormats.EAN_8,
            Html5QrcodeSupportedFormats.UPC_A,
            Html5QrcodeSupportedFormats.UPC_E,
            Html5QrcodeSupportedFormats.CODE_128,
            Html5QrcodeSupportedFormats.CODE_39,
            Html5QrcodeSupportedFormats.ITF,
          ],
        }
      )

      scannerRef.current.render(
        (decodedText) => {
          console.log('Barcode detected:', decodedText)

          // Validate the scanned barcode format
          if (/^\d{4}$/.test(decodedText)) {
            setBarcode(decodedText)
            setIsScanning(false)
            try { scannerRef.current?.clear() } catch {}
            toast({
              title: "Barcode Scanned!",
              description: `Detected: ${decodedText}. Click "Process Barcode" to continue.`,
            })
            // Automatically process the scanned barcode
            setTimeout(() => {
              handleScan({ preventDefault: () => {} } as React.FormEvent, { auto: true })
            }, 500)
          } else {
            toast({
              title: "Invalid Barcode",
              description: "Scanned code must be 4 digits. Please try again.",
              variant: "destructive",
            })
          }
        },
        (_errorMessage) => {
          // Ignore frequent scan failures/noise
        }
      )
    } catch (error) {
      console.error("Failed to start barcode scanning:", error)
      toast({
        title: "Scanning Error",
        description: "Failed to start barcode scanning. Please try again.",
        variant: "destructive",
      })
      setIsScanning(false)
    }
  }

  const stopBarcodeScanning = () => {
    if (scannerRef.current) {
      try { scannerRef.current.clear() } catch {}
      scannerRef.current = null
    }
    setIsScanning(false)
  }

  const captureFromCamera = () => {
    if (isScanning) {
      stopBarcodeScanning()
    } else {
      startBarcodeScanning()
    }
  }

  const handleScan = async (
    e: React.FormEvent | null,
    options?: { auto?: boolean }
  ) => {
    if (e) e.preventDefault()

    if (!barcode.trim()) {
      toast({
        title: "Error",
        description: "Please enter or scan a barcode",
        variant: "destructive",
      })
      return
    }

    // Validate barcode format (should be 4 digits)
    if (!/^\d{4}$/.test(barcode.trim())) {
      toast({
        title: "Invalid Barcode Format",
        description: "Barcode must be exactly 4 digits (e.g., 1001, 2001, 3001)",
        variant: "destructive",
      })
      return
    }

    setLoading(true)
    setResult(null)

    try {
      const response = await fetch("/api/scan", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          barcode: barcode.trim(),
          userId: sessionStorage.getItem("userId"),
          userRole: userRole,
        }),
      })

      const data = await response.json()
      setResult(data)

      if (response.ok) {
        toast({
          title: "Success",
          description: data.message,
        })
        setBarcode("")
        // Auto-award apples on successful scan when triggered by camera
        if (options?.auto) {
          if (data?.type === "student" && data?.studentId) {
            await handleAddApples(1, { type: "student", id: data.studentId })
          } else if (data?.type === "assistant" && data?.assistantId) {
            await handleAddApples(150, { type: "assistant", id: data.assistantId }, true)
          }
        }
      } else {
        toast({
          title: "Scan Failed",
          description: data.message,
          variant: "destructive",
        })
      }
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to process scan",
        variant: "destructive",
      })
    } finally {
      setLoading(false)
    }
  }

  const handleAddApples = async (
    amount: number,
    target?: { type: "student" | "assistant"; id: string },
    isSessionAttendance?: boolean
  ) => {
    if (!result && !target) return

    setApplesLoading(true)
    try {
      const effectiveType = target?.type || (result?.type as "student" | "assistant")
      const effectiveId = target?.id || (result?.type === "student" ? result?.studentId : result?.assistantId)
      if (!effectiveType || !effectiveId) throw new Error("Missing target to update apples")

      const endpoint =
        effectiveType === "student"
          ? `/api/students/${effectiveId}/add-apples`
          : `/api/assistants/${effectiveId}/add-apples`

      const bodyData: { apples: number; adminId: string | null; isSessionAttendance?: boolean } = {
        apples: amount,
        adminId: sessionStorage.getItem("userId"),
      }

      if (effectiveType === "assistant" && isSessionAttendance) {
        bodyData.isSessionAttendance = true
      }

      const response = await fetch(endpoint, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(bodyData),
      })

      const data = await response.json()

      if (response.ok) {
        toast({
          title: "Success",
          description: data.message || `${amount > 0 ? "Added" : "Subtracted"} ${Math.abs(data.applesAdded || amount)} apples`,
        })
        if (result) {
          setResult({
            ...result,
            apples: data.apples,
          })
        }
      } else {
        toast({
          title: "Error",
          description: data.message,
          variant: "destructive",
        })
      }
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to update apples",
        variant: "destructive",
      })
    } finally {
      setApplesLoading(false)
    }
  }

  return (
    <main className="min-h-screen bg-gradient-to-br from-slate-950 via-slate-900 to-slate-950 p-4">
      <div className="max-w-2xl mx-auto pt-4">
        {/* Header */}
        <div className="text-center mb-6">
          <div className="flex justify-center gap-2 mb-4">
            <span className="text-4xl">ðŸŽ</span>
          </div>
          <h1 className="text-3xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-green-400 to-emerald-500 mb-1">
            Barcode Scanner
          </h1>
          <p className="text-slate-300 text-sm">
            Scanning as: {userName} ({userRole})
          </p>
        </div>

        {/* Camera Section */}
        {(cameraActive || cameraLoading) && (
          <Card className="mb-6 border-slate-700 bg-slate-800/50">
            <CardHeader>
              <CardTitle className="text-slate-100">Camera View</CardTitle>
              <CardDescription className="text-slate-400">
                {cameraLoading ? "Starting camera..." : isScanning ? "Point camera at barcode to scan automatically" : "Camera ready - click 'Start Scanning' to scan barcodes"}
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
                <div id="scanner-container" className="relative bg-black rounded-lg overflow-hidden min-h-[256px] flex items-center justify-center">
                {!cameraLoading && (
                  <div className="absolute bottom-2 left-0 right-0 text-center text-white text-xs pointer-events-none">
                    {isScanning ? (
                      <div className="text-green-300">Point camera at a 4-digit barcode</div>
                    ) : (
                      <div className="text-slate-300">Click 'Start Scanning' to scan barcodes</div>
                    )}
                  </div>
                )}
                {cameraLoading && (
                  <div className="flex items-center justify-center h-64">
                    <div className="text-center">
                      <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-500 mx-auto mb-4"></div>
                      <div className="text-white text-sm">Starting camera...</div>
                    </div>
                  </div>
                )}
              </div>
              <div className="flex gap-2">
                <Button 
                  onClick={captureFromCamera} 
                  className="flex-1 bg-blue-600 hover:bg-blue-700"
                  disabled={cameraLoading}
                >
                  {isScanning ? "ðŸ›‘ Stop Scanning" : "ðŸ“· Start Scanning"}
                </Button>
                <Button
                  onClick={() => {
                    stopCamera()
                    setTimeout(() => startCamera(), 100)
                  }}
                  variant="outline"
                  className="flex-1 border-yellow-600 text-yellow-400 hover:bg-yellow-500/10"
                  disabled={cameraLoading}
                >
                  ðŸ”„ Refresh Camera
                </Button>
                <Button
                  onClick={stopCamera}
                  variant="outline"
                  className="flex-1 border-slate-600 text-slate-100 hover:bg-slate-700 bg-transparent"
                  disabled={cameraLoading}
                >
                  Close Camera
                </Button>
              </div>
              <div className="text-xs text-slate-400 space-y-1">
                <div className="font-semibold">How to use:</div>
                <div>â€¢ Click "Start Scanning" to begin automatic barcode detection</div>
                <div>â€¢ Point camera at 4-digit barcodes (1001, 2001, 3001, etc.)</div>
                <div>â€¢ Scanned barcodes will be automatically processed</div>
                <div>â€¢ You can also manually enter barcodes below</div>
                <div className="font-semibold mt-2">Troubleshooting:</div>
                <div>â€¢ If camera shows black screen, try refreshing the page</div>
                <div>â€¢ Ensure camera permissions are granted</div>
                <div>â€¢ Make sure you're using HTTPS (required for camera access)</div>
              </div>
            </CardContent>
          </Card>
        )}

        {/* Scanner Input */}
        <Card className="mb-6 border-slate-700 bg-slate-800/50 backdrop-blur">
          <CardHeader>
            <CardTitle className="text-slate-100">Scan Barcode</CardTitle>
            <CardDescription className="text-slate-400">
              {userRole === "admin" ? "Use camera to scan or manually enter student/admin barcodes" : "Use camera to scan or manually enter student barcodes"}
            </CardDescription>
          </CardHeader>
          <CardContent>
            <form onSubmit={handleScan} className="space-y-4">
              <div className="space-y-2">
                <label className="text-sm font-medium text-slate-200">Barcode</label>
                <Input
                  type="text"
                  placeholder="Enter 4-digit barcode (e.g., 1001, 2001, 3001)..."
                  value={barcode}
                  onChange={(e) => {
                    // Only allow digits and limit to 4 characters
                    const value = e.target.value.replace(/\D/g, '').slice(0, 4)
                    setBarcode(value)
                  }}
                  className="bg-slate-700 border-slate-600 text-slate-100 placeholder-slate-400"
                  disabled={loading}
                  autoFocus
                  maxLength={4}
                />
                <p className="text-xs text-slate-400">
                  Student barcodes start with 1, Admin with 2, Assistant with 3
                </p>
                <div className="text-xs text-slate-500 space-y-1">
                  <div>Sample barcodes for testing:</div>
                  <div className="flex gap-2 flex-wrap">
                    <button
                      type="button"
                      onClick={() => setBarcode("1001")}
                      className="px-2 py-1 bg-slate-700 rounded text-slate-300 hover:bg-slate-600"
                    >
                      1001 (Student)
                    </button>
                    <button
                      type="button"
                      onClick={() => setBarcode("2001")}
                      className="px-2 py-1 bg-slate-700 rounded text-slate-300 hover:bg-slate-600"
                    >
                      2001 (Admin)
                    </button>
                    <button
                      type="button"
                      onClick={() => setBarcode("3001")}
                      className="px-2 py-1 bg-slate-700 rounded text-slate-300 hover:bg-slate-600"
                    >
                      3001 (Assistant)
                    </button>
                  </div>
                </div>
              </div>

              <div className="flex gap-2">
                <Button
                  type="submit"
                  className="flex-1 bg-gradient-to-r from-green-500 to-emerald-600 hover:from-green-600 hover:to-emerald-700 text-white font-semibold"
                  disabled={loading || barcode.length !== 4}
                >
                  {loading ? "Processing..." : "Process Barcode"}
                </Button>
                {!cameraActive && !cameraLoading ? (
                  <Button
                    type="button"
                    onClick={async () => { await startCamera(); await startBarcodeScanning() }}
                    className="flex-1 bg-blue-600 hover:bg-blue-700 text-white"
                  >
                    ðŸ“· Open Camera
                  </Button>
                ) : cameraLoading ? (
                  <Button
                    type="button"
                    disabled
                    className="flex-1 bg-blue-600/50 text-white"
                  >
                    <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                    Starting Camera...
                  </Button>
                ) : null}
              </div>
            </form>
          </CardContent>
        </Card>

        {/* Result */}
        {result && (
          <Card
            className={`mb-6 border-2 ${
              result.success ? "border-green-500 bg-green-500/10" : "border-red-500 bg-red-500/10"
            }`}
          >
            <CardContent className="pt-6 space-y-4">
              <p className={`text-center font-semibold ${result.success ? "text-green-400" : "text-red-400"}`}>
                {result.message}
              </p>
              {result.name && (
                <p className="text-center text-slate-300">
                  {result.name} {result.type && `(${result.type})`}
                </p>
              )}
              {result.apples !== undefined && (
                <p className="text-center text-emerald-400 font-bold">ðŸŽ {result.apples} apples</p>
              )}

              {(result.type === "student" || result.type === "assistant") && (
                <div className="space-y-3 pt-4 border-t border-slate-600">
                  {result.type === "student" && (
                    <>
                      <p className="text-sm text-slate-300 font-semibold">Add Apples:</p>
                      <div className="grid grid-cols-2 gap-2">
                        <Button
                          onClick={() => handleAddApples(1)}
                          disabled={applesLoading}
                          className="bg-green-600 hover:bg-green-700 text-white"
                        >
                          +1 Apple
                        </Button>
                        <Button
                          onClick={() => handleAddApples(5)}
                          disabled={applesLoading}
                          className="bg-green-600 hover:bg-green-700 text-white"
                        >
                          +5 Apples
                        </Button>
                        <Button
                          onClick={() => handleAddApples(20)}
                          disabled={applesLoading}
                          className="bg-green-600 hover:bg-green-700 text-white"
                        >
                          +20 Apples
                        </Button>
                        <Button
                          onClick={() => handleAddApples(-10)}
                          disabled={applesLoading}
                          className="bg-red-600 hover:bg-red-700 text-white"
                        >
                          -10 Apples
                        </Button>
                      </div>
                    </>
                  )}

                  {result.type === "assistant" && (
                    <>
                      <p className="text-sm text-slate-300 font-semibold">Manage Apples:</p>
                      <div className="grid grid-cols-2 gap-2">
                        <Button
                          onClick={() => handleAddApples(150, undefined, true)}
                          disabled={applesLoading}
                          className="bg-blue-600 hover:bg-blue-700 text-white font-semibold"
                        >
                          âœ“ Attend Session
                        </Button>
                        <Button
                          onClick={() => handleAddApples(-10)}
                          disabled={applesLoading}
                          className="bg-red-600 hover:bg-red-700 text-white"
                        >
                          - Subtract
                        </Button>
                      </div>
                    </>
                  )}
                </div>
              )}
            </CardContent>
          </Card>
        )}

        {/* Navigation */}
        <div className="flex gap-2">
          <Link href={`/dashboard/${userName}?role=${userRole}`} className="flex-1">
            <Button
              variant="outline"
              className="w-full border-slate-600 text-slate-100 hover:bg-slate-700 bg-transparent"
            >
              View Dashboard
            </Button>
          </Link>
          <Button
            onClick={() => {
              sessionStorage.clear()
              router.push("/login")
            }}
            variant="outline"
            className="flex-1 border-red-600 text-red-400 hover:bg-red-500/10"
          >
            Logout
          </Button>
        </div>
      </div>
    </main>
  )
}

app/api/auth/logout/route.ts.content:
import { type NextRequest, NextResponse } from "next/server"

export async function POST(request: NextRequest) {
  const response = NextResponse.json({ message: "Logged out successfully" }, { status: 200 })

  response.cookies.set("auth_user", "", {
    httpOnly: true,
    secure: process.env.NODE_ENV === "production",
    sameSite: "lax",
    maxAge: 0,
  })

  return response
}

app/api/students/[id]/add-apples/route.t.content:
import { type NextRequest, NextResponse } from "next/server"
import { getSupabaseClient } from "@/lib/db"

export async function POST(request: NextRequest, { params }: { params: { id: string } }) {
  try {
    const { apples, adminId } = await request.json()
    const studentId = params.id

    if (apples === undefined || apples === null) {
      return NextResponse.json({ message: "Invalid apple amount" }, { status: 400 })
    }

    const supabase = getSupabaseClient()
    const { data: student, error } = await supabase
      .from("students")
      .select("id, name, apples")
      .eq("id", studentId)
      .single()

    if (error || !student) {
      return NextResponse.json({ message: "Student not found" }, { status: 404 })
    }

    const newApples = Math.max(0, (student.apples || 0) + apples)

    const { data: updatedStudent } = await supabase
      .from("students")
      .update({ apples: newApples })
      .eq("id", studentId)
      .select()
      .single()

    // Log transaction
    await supabase.from("apple_transactions").insert({
      student_id: studentId,
      admin_id: adminId,
      apples_added: apples,
      reason: apples > 0 ? "Manual addition" : "Apple deduction",
    })

    return NextResponse.json({
      success: true,
      name: updatedStudent?.name,
      apples: updatedStudent?.apples,
      applesAdded: apples,
      message: `${apples > 0 ? "Added" : "Subtracted"} ${Math.abs(apples)} apples`,
    })
  } catch (error) {
    console.error("[v0] Add student apples error:", error)
    return NextResponse.json({ message: "Internal server error" }, { status: 500 })
  }
}

app/dashboard/[name]/page.tsx.content:
"use client"

import { useEffect, useState } from "react"
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card"
import { Button } from "@/components/ui/button"
import { useToast } from "@/hooks/use-toast"
import Link from "next/link"
import { useSearchParams, useRouter } from "next/navigation"

interface AssistantData {
  id: string
  name: string
  barcode: string
  apples: number
  role: string
  sessions: number
  bonusCount: number
  loyaltyHistory: Array<{ bonus_type: string; bonus_apples: number }>
}

interface StudentData {
  id: string
  name: string
  barcode: string
  apples: number
}

interface DashboardData {
  name: string
  barcode: string
  apples: number
  sessions: number
  loyaltyHistory: Array<{ bonus_type: string; bonus_apples: number; created_at: string }>
}

export default function Dashboard({ params }: { params: { name: string } }) {
  const [data, setData] = useState<DashboardData | AssistantData[] | StudentData[] | null>(null)
  const [loading, setLoading] = useState(true)
  const [isAdmin, setIsAdmin] = useState(false)
  const [viewType, setViewType] = useState<"assistants" | "students">("assistants")
  const [totalApples, setTotalApples] = useState(0)
  const [paying, setPaying] = useState(false)
  const { toast } = useToast()
  const searchParams = useSearchParams()
  const router = useRouter()
  const userRole = sessionStorage.getItem("userRole") || "assistant"
  const decodedName = decodeURIComponent(params.name)

  useEffect(() => {
    const userId = sessionStorage.getItem("userId")
    if (!userId) {
      router.push("/login")
      return
    }

    fetchData()
  }, [decodedName, userRole, viewType, router])

  const fetchData = async () => {
    try {
      const response = await fetch(
        `/api/dashboard/${encodeURIComponent(decodedName)}?role=${userRole}&viewType=${viewType}`,
      )
      const result = await response.json()

      if (!response.ok) {
        toast({
          title: "Error",
          description: result.message || "Failed to load data",
          variant: "destructive",
        })
        return
      }

      if (result.isAdmin) {
        setIsAdmin(true)
        setTotalApples(result.totalApples || 0)
        if (result.viewType === "students") {
          setData(result.students)
        } else {
          setData(result.assistants)
        }
      } else {
        setIsAdmin(false)
        setData(result)
      }
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to load data",
        variant: "destructive",
      })
    } finally {
      setLoading(false)
    }
  }

  const handlePayRewards = async () => {
    if (!window.confirm("Are you sure you want to reset all assistant scores to zero? This action cannot be undone.")) {
      return
    }

    setPaying(true)
    try {
      const userId = sessionStorage.getItem("userId")
      const response = await fetch("/api/assistants/pay-rewards", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ userId }),
      })

      const result = await response.json()

      if (!response.ok) {
        toast({
          title: "Error",
          description: result.message || "Failed to pay rewards",
          variant: "destructive",
        })
        return
      }

      toast({
        title: "Success",
        description: "All assistant scores have been reset to zero",
      })

      fetchData()
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to pay rewards",
        variant: "destructive",
      })
    } finally {
      setPaying(false)
    }
  }

  if (loading) {
    return (
      <main className="min-h-screen bg-gradient-to-br from-slate-950 via-slate-900 to-slate-950 p-4">
        <div className="max-w-4xl mx-auto pt-8">
          <p className="text-center text-slate-300">Loading...</p>
        </div>
      </main>
    )
  }

  if (!data) {
    return (
      <main className="min-h-screen bg-gradient-to-br from-slate-950 via-slate-900 to-slate-950 p-4">
        <div className="max-w-4xl mx-auto pt-8">
          <Card className="border-slate-700 bg-slate-800/50">
            <CardContent className="pt-6">
              <p className="text-center text-slate-300 mb-4">Data not found</p>
              <Link href="/scanner" className="block">
                <Button className="w-full bg-gradient-to-r from-green-500 to-emerald-600 hover:from-green-600 hover:to-emerald-700 text-white">
                  Back to Scanner
                </Button>
              </Link>
            </CardContent>
          </Card>
        </div>
      </main>
    )
  }

  if (isAdmin && Array.isArray(data)) {
    return (
      <main className="min-h-screen bg-gradient-to-br from-slate-950 via-slate-900 to-slate-950 p-4">
        <div className="max-w-6xl mx-auto pt-4">
          <div className="text-center mb-6">
            <div className="flex justify-center gap-2 mb-2">
              <span className="text-3xl">ðŸŽ</span>
            </div>
            <h1 className="text-3xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-green-400 to-emerald-500 mb-1">
              Admin Dashboard
            </h1>
            <p className="text-sm text-slate-300">
              {viewType === "assistants" ? "All Assistants Overview" : "All Students Overview"}
            </p>
          </div>

          <div className="flex gap-2 mb-6 justify-center">
            <Button
              onClick={() => setViewType("assistants")}
              className={`${
                viewType === "assistants"
                  ? "bg-gradient-to-r from-green-500 to-emerald-600 text-white"
                  : "bg-slate-700 text-slate-300 hover:bg-slate-600"
              }`}
            >
              Assistants
            </Button>
            <Button
              onClick={() => setViewType("students")}
              className={`${
                viewType === "students"
                  ? "bg-gradient-to-r from-green-500 to-emerald-600 text-white"
                  : "bg-slate-700 text-slate-300 hover:bg-slate-600"
              }`}
            >
              Students
            </Button>
          </div>

          <Card className="mb-6 border-emerald-500/50 bg-slate-800/50">
            <CardHeader>
              <CardTitle className="text-emerald-400">
                Total {viewType === "assistants" ? "Assistant" : "Student"} Apples
              </CardTitle>
            </CardHeader>
            <CardContent>
              <p className="text-4xl font-bold text-emerald-400">{totalApples}</p>
            </CardContent>
          </Card>

          {viewType === "assistants" && (
            <div className="mb-6">
              <Button
                onClick={handlePayRewards}
                disabled={paying}
                className="w-full bg-gradient-to-r from-orange-500 to-red-600 hover:from-orange-600 hover:to-red-700 text-white font-semibold"
              >
                {paying ? "Processing..." : "Pay Rewards (Reset All Scores)"}
              </Button>
            </div>
          )}

          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4 mb-6">
            {data.map((item) => (
              <Card
                key={item.id}
                className="border-slate-700 bg-slate-800/50 hover:border-emerald-500/50 transition-colors"
              >
                <CardHeader className="pb-3">
                  <CardTitle className="text-emerald-400 text-lg">{item.name}</CardTitle>
                  <CardDescription className="text-slate-400 text-xs">ID: {item.barcode}</CardDescription>
                </CardHeader>
                <CardContent className="space-y-3">
                  <div className="flex justify-between items-center">
                    <span className="text-slate-300 text-sm">Total Apples:</span>
                    <span className="text-emerald-400 font-bold text-lg">{item.apples}</span>
                  </div>
                  {viewType === "assistants" && "sessions" in item && (
                    <>
                      <div className="grid grid-cols-2 gap-2">
                        <div className="bg-slate-700/50 p-2 rounded">
                          <p className="text-xs text-slate-400">Sessions</p>
                          <p className="text-lg font-bold text-blue-400">{item.sessions}</p>
                        </div>
                        <div className="bg-slate-700/50 p-2 rounded">
                          <p className="text-xs text-slate-400">Bonuses</p>
                          <p className="text-lg font-bold text-orange-400">{item.bonusCount}</p>
                        </div>
                      </div>
                      {item.loyaltyHistory.length > 0 && (
                        <div className="bg-slate-700/30 p-2 rounded text-xs text-slate-300">
                          <p className="font-semibold mb-1">Recent Bonuses:</p>
                          <div className="space-y-1">
                            {item.loyaltyHistory.slice(0, 3).map((history, idx) => (
                              <div key={idx} className="flex justify-between">
                                <span>{history.bonus_type}:</span>
                                <span className="text-orange-400">+{history.bonus_apples}</span>
                              </div>
                            ))}
                          </div>
                        </div>
                      )}
                    </>
                  )}
                </CardContent>
              </Card>
            ))}
          </div>

          {data.length === 0 && (
            <Card className="border-slate-700 bg-slate-800/50">
              <CardContent className="pt-6">
                <p className="text-center text-slate-300">
                  No {viewType === "assistants" ? "assistants" : "students"} found
                </p>
              </CardContent>
            </Card>
          )}

          <div className="flex gap-2">
            <Link href="/scanner" className="flex-1">
              <Button className="w-full bg-gradient-to-r from-green-500 to-emerald-600 hover:from-green-600 hover:to-emerald-700 text-white font-semibold">
                Go to Scanner
              </Button>
            </Link>
            <Button
              onClick={() => {
                sessionStorage.clear()
                window.location.href = "/login"
              }}
              variant="outline"
              className="flex-1 border-red-600 text-red-400 hover:bg-red-500/10"
            >
              Logout
            </Button>
          </div>
        </div>
      </main>
    )
  }

  const userData = data as DashboardData
  const sessions = userData.sessions
  const bonusCount = userData.loyaltyHistory.length

  return (
    <main className="min-h-screen bg-gradient-to-br from-slate-950 via-slate-900 to-slate-950 p-4">
      <div className="max-w-2xl mx-auto pt-4">
        <div className="text-center mb-6">
          <div className="flex justify-center gap-2 mb-2">
            <span className="text-3xl">ðŸŽ</span>
          </div>
          <h1 className="text-3xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-green-400 to-emerald-500 mb-1">
            Dashboard
          </h1>
          <p className="text-sm text-slate-300">Welcome, {userData.name}!</p>
        </div>

        {/* Score Card */}
        <Card className="mb-6 border-emerald-500/50 bg-slate-800/50 backdrop-blur">
          <CardHeader className="bg-slate-800/30">
            <CardTitle className="text-emerald-400">Your Score</CardTitle>
            <CardDescription className="text-slate-400">Total apples earned</CardDescription>
          </CardHeader>
          <CardContent className="pt-8">
            <div className="text-center">
              <p className="text-6xl font-bold text-emerald-400 mb-2">{userData.apples}</p>
              <p className="text-slate-300">Keep scanning to earn more apples!</p>
            </div>
          </CardContent>
        </Card>

        {/* Stats Grid */}
        <div className="grid grid-cols-2 gap-4 mb-6">
          <Card className="border-slate-700 bg-slate-800/50">
            <CardHeader className="pb-3">
              <CardTitle className="text-sm font-medium text-slate-400">Sessions</CardTitle>
            </CardHeader>
            <CardContent>
              <p className="text-3xl font-bold text-emerald-400">{sessions}</p>
              <p className="text-xs text-slate-400 mt-1">sessions attended</p>
            </CardContent>
          </Card>

          <Card className="border-slate-700 bg-slate-800/50">
            <CardHeader className="pb-3">
              <CardTitle className="text-sm font-medium text-slate-400">Bonuses</CardTitle>
            </CardHeader>
            <CardContent>
              <p className="text-3xl font-bold text-orange-400">{bonusCount}</p>
              <p className="text-xs text-slate-400 mt-1">bonuses earned</p>
            </CardContent>
          </Card>
        </div>

        {/* Loyalty Bonus Info */}
        <Card className="mb-6 border-orange-500/50 bg-slate-800/50">
          <CardHeader>
            <CardTitle className="text-orange-400">Loyalty Bonus Info</CardTitle>
          </CardHeader>
          <CardContent className="text-sm text-slate-300">
            <p className="mb-2">Reach 4 sessions (600 apples) to earn a 50 apple bonus automatically!</p>
            <p className="text-xs text-slate-400">Current sessions: {sessions} / 4</p>
            <div className="mt-3 bg-slate-700/50 rounded-full h-2 overflow-hidden">
              <div
                className="bg-gradient-to-r from-green-500 to-emerald-600 h-full transition-all"
                style={{ width: `${Math.min((sessions / 4) * 100, 100)}%` }}
              />
            </div>
          </CardContent>
        </Card>

        {/* Bonus History */}
        {userData.loyaltyHistory.length > 0 && (
          <Card className="mb-6 border-slate-700 bg-slate-800/50">
            <CardHeader>
              <CardTitle className="text-slate-100">Bonus History</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-2">
                {userData.loyaltyHistory.map((bonus, idx) => (
                  <div
                    key={idx}
                    className="flex justify-between items-center p-2 bg-slate-700/50 rounded border border-slate-600"
                  >
                    <span className="text-sm text-slate-300">{bonus.bonus_type}</span>
                    <span className="text-sm font-medium text-orange-400">+{bonus.bonus_apples}</span>
                  </div>
                ))}
              </div>
            </CardContent>
          </Card>
        )}

        <div className="flex gap-2">
          <Link href="/scanner" className="flex-1">
            <Button className="w-full bg-gradient-to-r from-green-500 to-emerald-600 hover:from-green-600 hover:to-emerald-700 text-white font-semibold">
              Go to Scanner
            </Button>
          </Link>
          <Button
            onClick={() => {
              sessionStorage.clear()
              window.location.href = "/login"
            }}
            variant="outline"
            className="flex-1 border-red-600 text-red-400 hover:bg-red-500/10"
          >
            Logout
          </Button>
        </div>
      </div>
    </main>
  )
}

